import jsPDF from 'jspdf';
import 'jspdf-autotable';
import FreeSerif from './FreeSerif-normal.js';

class ReceiptGenerator {
  constructor() {
    this.doc = new jsPDF('p', 'mm', [200,200]);
    this.doc.addFileToVFS('FreeSerif.ttf', FreeSerif);
    this.doc.addFont('FreeSerif.ttf', 'FreeSerif', 'normal');
    this.doc.setFont('FreeSerif');
  }

  generateReceipt(data) {
    const logoBase64 = "data:image/png;base64,";
    const { customerNo, processRef, subeCode, processDate, taxOffice, currency, amount, explanation, customerName, processBank } = data;

    // Dekont Başlığı
    this.doc.addImage(logoBase64, "PNG", 15, 10, 35, 35);
    this.doc.setFontSize(16);
    this.doc.text('e-Dekont', 105, 20, { align: 'center' });

    // Muhasebe İşlem Dekontu
    this.doc.setFontSize(12);
    this.doc.text('MUHASEBE İŞLEM DEKONTU', 105, 30, { align: 'center' });

    // Genel Bilgiler Tablosu
    const generalInfo = [
      ['ŞUBE KODU:', subeCode],
      ['MÜŞTERİ NO:', customerNo],
      ['VERGİ DAİRESİ:', taxOffice],
      ['İŞLEM REF:', processRef],
      ['İŞLEM TARİHİ:', processDate],
      ['DÖVİZ CİNSİ:', currency],
      ['İŞLEM TUTARI:', amount],
      ['İŞLEM YERİ:', processBank],
    ];

    this.doc.autoTable({
      head: [['Bilgi', 'Detay']],
      body: generalInfo,
      startY: 40,
      theme: 'grid',
      styles: {
        font: 'FreeSerif',
        fontSize: 10,
      },
      headStyles: {
        fillColor: '#00333D', // Başlık arka plan rengi
        textColor: '#FFFFFF', // Başlık metin rengi
      },
      bodyStyles: {
        fillColor: '#F5F5F5', // Satır arka plan rengi (isteğe bağlı)
        textColor: '#000000', // Satır metin rengi
      },
    });

    // Açıklama ve Müşteri Bilgisi
    this.doc.text(`AÇIKLAMA: ${explanation}`, 14, this.doc.lastAutoTable.finalY + 10);
    this.doc.text(`MÜŞTERİ ADI: ${customerName}`, 14, this.doc.lastAutoTable.finalY + 20);

    // Dipnot
    this.doc.setFontSize(10);
    this.doc.text(
      'e-Dekont izni kapsamında elektronik ortamda üretilmiştir.',
      105,
      this.doc.lastAutoTable.finalY + 40,
      { align: 'center' }
    );
    this.doc.text(
      'Prisma Bankası A.Ş. - Merkez, Osmaniye',
      105,
      this.doc.lastAutoTable.finalY + 50,
      { align: 'center' }
    );

    return this.doc;
  }

  downloadPDF(fileName) {
    this.doc.save(`${fileName}.pdf`);
  }
}

export default ReceiptGenerator;
